definition.script:
node{
  stage('SCM'){
      checkout([
          $class: 'SubversionSCM', 
          additionalCredentials: [],
          excludedCommitMessages: '', 
          excludedRegions: '', 
          excludedRevprop: '', 
          excludedUsers: '', 
          filterChangelog: false, 
          ignoreDirPropChanges: false, 
          includedRegions: '', 
          locations: [
              [
                  cancelProcessOnExternalsFail: true, 
                  credentialsId: '9838a34a-d7c8-4401-b4eb-f391afc45ad6', 
                  depthOption: 'infinity', 
                  ignoreExternalsOption: true, 
                  local: '.', 
                  remote: 'https://svn.detran.goias.intra/svn/java/Escrit처rio Processos Detran/Gest찾o de TI/scd/Tags/${Tag}/4%20-%20Implementa%C3%A7%C3%A3o/scd'
              ]
          ], 
          quietOperation: false,
          workspaceUpdater: [$class: 'CheckoutUpdater']
      ])
  }
  stage('QA') {
      stage('QA-Compile') {
          withMaven(maven: 'maven-3'){
              sh 'mvn -X -Duser.home=/opt/.jenkins clean package -Phomolog -U'
          }
      }
      stage('QA-Analisys') {
          withSonarQubeEnv('sonar-6'){
              withMaven(maven: 'maven-3'){
                  sh 'mvn -X -Duser.home=/opt/.jenkins -Phomolog sonar:sonar'
              }
          }
      }
      stage('QA-Quality Gate'){
          timeout (time: 5, unit: 'MINUTES') {
              def qg = waitForQualityGate()          
              if (qg.status != 'OK') {
                  error "Pipeline aborted due to quality gate failure: ${qg.status}"
              }
          }
      }
  }

  stage('Build'){
      withMaven(maven: 'maven-3'){
          sh 'mvn -X -Duser.home=/opt/.jenkins -Phomolog package'
      }
  }

  stage('Deploy-copy'){
      withMaven(maven: 'maven-3'){

          sh 'cd /var/lib/jenkins/workspace/SCD-HOM/detran-scd-web/target && cp detran-scd-web.war /var/lib/jenkins/workspace/SCD-HOM/target/ && cd /var/lib/jenkins/workspace/SCD-HOM/detran-scd-ear/target && cp detran-scd-ear.ear /var/lib/jenkins/workspace/SCD-HOM/target/'
      }
  }
stage('Deploy DETRAN WAR'){
        withCredentials([usernamePassword(credentialsId: 'jboss-eap6', passwordVariable: 'PASSWORD', usernameVariable: 'USERNAME')]) {
            def jbossCli="/opt/EAP-7.0.9/bin/jboss-cli.sh --connect --controller=10.110.30.75"
            def jbossStatus=""
            def jbossExists=""
            stage ('Deploy-Init-WAR'){
                jbossStatus=sh( returnStdout: true, script: "$jbossCli --user=${USERNAME} --password=${PASSWORD} --command='/server-group=Homolog:read-resource'")
                jbossExists=jbossStatus.toLowerCase().contains("detran-scd-web.war")
            }
            if (jbossExists) {
                stage('Deploy-Replace-WAR'){
                    jbossStatus=sh( returnStdout: true, script: "$jbossCli --user=${USERNAME} --password=${PASSWORD} --command='deploy target/detran-scd-web.war --force --headers={rollout Homolog(rolling-to-servers=false)}'")
                    echo jbossStatus
                }
            } else {
                stage('Deploy-New-WAR'){
                        jbossStatus=sh( returnStdout: true, script: "$jbossCli --user=${USERNAME} --password=${PASSWORD} --command='deploy target/detran-scd-web.war --server-groups=Homolog  --name=detran-scd-web.war'")
                        echo jbossStatus
                }
            }
        stage('Deploy DETRAN EAR'){
            stage ('Deploy-Init-EAR'){
                jbossStatus=sh( returnStdout: true, script: "$jbossCli --user=${USERNAME} --password=${PASSWORD} --command='/server-group=Homolog:read-resource'")
                jbossExists=jbossStatus.toLowerCase().contains("detran-scd-ear.ear")
            }
            if (jbossExists) {
                stage('Deploy-Replace-EAR'){
                    jbossStatus=sh( returnStdout: true, script: "$jbossCli --user=${USERNAME} --password=${PASSWORD} --command='deploy target/detran-scd-ear.ear --force --headers={rollout Homolog(rolling-to-servers=false)}'")
                    echo jbossStatus
                }
            } else {
                stage('Deploy-New-EAR'){
                        jbossStatus=sh( returnStdout: true, script: "$jbossCli --user=${USERNAME} --password=${PASSWORD} --command='deploy target/detran-scd-ear.ear --server-groups=Homolog  --name=detran-scd-ear.ear'")
                        echo jbossStatus
                }
            }
        }

        }
    }

}
