definition.script:
node{
  stage('SCM'){
      checkout([
          $class: 'SubversionSCM', 
          additionalCredentials: [],
          excludedCommitMessages: '', 
          excludedRegions: '', 
          excludedRevprop: '', 
          excludedUsers: '', 
          filterChangelog: false, 
          ignoreDirPropChanges: false, 
          includedRegions: '', 
          locations: [
              [
                  cancelProcessOnExternalsFail: true, 
                  credentialsId: '9838a34a-d7c8-4401-b4eb-f391afc45ad6', 
                  depthOption: 'infinity', 
                  ignoreExternalsOption: true, 
                  local: '.', 
                  remote: 'https://svn.detran.goias.intra/svn/java/Escrit처rio Processos Detran/Gest찾o de TI/srf/Tags/${Tag}/4%20-%20Implementa%C3%A7%C3%A3o/srf'
              ]
          ], 
          quietOperation: false,
          workspaceUpdater: [$class: 'CheckoutUpdater']
      ])
  }
  stage('QA') {
      stage('QA-Compile') {
          withMaven(maven: 'maven-3',jdk:'JDK7'){
              sh 'mvn -X -Duser.home=/opt/.jenkins clean package -Phomolog -U'
          }
      }
      stage('QA-Analisys') {
          withSonarQubeEnv('sonar-6'){
              withMaven(maven: 'maven-3'){
                  sh 'mvn -X -Duser.home=/opt/.jenkins -Phomolog sonar:sonar'
              }
          }
      }
      stage('QA-Quality Gate'){
          timeout (time: 5, unit: 'MINUTES') {
              def qg = waitForQualityGate()          
              if (qg.status != 'OK') {
                  error "Pipeline aborted due to quality gate failure: ${qg.status}"
              }
          }
      }
  }

  stage('Build'){
      withMaven(maven: 'maven-3',jdk:'JDK7'){
          sh 'mvn -X -Duser.home=/opt/.jenkins -Phomolog package'
      }
  }


  stage('Deploy'){
      withCredentials([usernamePassword(credentialsId: 'jboss-eap6', passwordVariable: 'PASSWORD', usernameVariable: 'USERNAME')]) {
          def jbossCli="/opt/jboss-6.4.21/bin/jboss-cli.sh --connect --controller=10.110.30.70"
          def jbossStatus=""
          def jbossExists=""
          stage ('Deploy-Init'){
              jbossStatus=sh( returnStdout: true, script: "$jbossCli --user=${USERNAME} --password=${PASSWORD} --command='/server-group=Homolog-Detran:read-resource'")
              jbossExists=jbossStatus.toLowerCase().contains("srf.war")
          }
          if (jbossExists) {
              stage('Deploy-Replace'){
                  jbossStatus=sh( returnStdout: true, script: "$jbossCli --user=${USERNAME} --password=${PASSWORD} --command='deploy target/srf.war --force --headers={rollout Homolog-Detran(rolling-to-servers=false)}'")
                  echo jbossStatus
              }
          } else {
              stage('Deploy-New'){
                      jbossStatus=sh( returnStdout: true, script: "$jbossCli --user=${USERNAME} --password=${PASSWORD} --command='deploy target/srf.war --server-groups=Homolog-Detran  --name=srf.war'")
                      echo jbossStatus
              }
          }
      }
  }
}
